mapper.select:
SELECT B.RN, B.BOARD_ID, B.TITLE, B.CONTENT, B.VIEW_COUNT, B.CREATE_ID, B.UPDATE_ID, B.CREATE_DT, B.UPDATE_DT, B.DEL_YN FROM( SELECT ROWNUM AS RN, A.BOARD_ID, A.TITLE, A.CONTENT, A.VIEW_COUNT, A.CREATE_ID, A.UPDATE_ID, TO_CHAR(A.CREATE_DT, 'YYYY-MM-DD') AS CREATE_DT, TO_CHAR(A.UPDATE_DT, 'YYYY-MM-DD') AS UPDATE_DT, A.DEL_YN FROM NEWS_BOARD A
A.DEL_YN = 'N'
AND
A.TITLE LIKE '%'||#{searchText}||'%'
A.CREATE_ID LIKE '%'||#{searchText}||'%'
) B WHERE B.RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE NEWS_BOARD
SET VIEW_COUNT = #{viewCount}
WHERE BOARD_ID = #{boardId}
