mapper.update:
UPDATE users
SET password = #{encodedPassword}
WHERE user_id = #{userId}

mapper.select:
SELECT *
FROM users
WHERE username = #{name}
  AND phonenumber = #{phone}
  AND birth_date = #{birthDate}
  AND email = #{email}

SELECT *
FROM users
WHERE username = #{name}
  AND user_id = #{userId}
  AND phonenumber = #{phone}
  AND BIRTH_DATE = #{birthDate, jdbcType=DATE}

SELECT * FROM USERS
WHERE USER_ID = #{userId}
  AND USERNAME = #{username}
  AND PHONENUMBER = #{phonenumber}
  AND TO_CHAR(BIRTH_DATE, 'YYYY-MM-DD') = #{birthDate,jdbcType=VARCHAR}
  AND EMAIL = #{email}
  AND DEL_YN = 'N'
